import React, { useEffect, useState } from "react";
import NewsItems from "./NewsItems";
import Spinner from "./Spinner";
import PropTypes from "prop-types";
import InfiniteScroll from "react-infinite-scroll-component";

const News = (props) => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [totalResults, setTotalResults] = useState(0);

  const capitalizeFirstLetter = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  };

  const updateNews = async () => {
    props.setProgress(10);
    const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=${props.apiKey}&page=${page}&pageSize=${props.pageSize}`;
    setLoading(true);
    try {
      let data = await fetch(url);
      props.setProgress(30);
      let parsedData = await data.json();
      props.setProgress(70);
      setArticles(parsedData.articles || []);
      setTotalResults(parsedData.totalResults || 0);
      setLoading(false);
      props.setProgress(100);
    } catch (error) {
      console.error("Failed to fetch news:", error);
      setLoading(false);
    }
  };

  const fetchMoreData = async () => {
    const nextPage = page + 1;
    const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=${props.apiKey}&page=${nextPage}&pageSize=${props.pageSize}`;
    setPage(nextPage);
    try {
      let data = await fetch(url);
      let parsedData = await data.json();
      setArticles((prevArticles) =>
        prevArticles.concat(parsedData.articles || [])
      );
      setTotalResults(parsedData.totalResults || 0);
    } catch (error) {
      console.error("Failed to fetch more news:", error);
    }
  };

  useEffect(() => {
    document.title = `${capitalizeFirstLetter(props.category)} - AI News Hub`;
    updateNews();
    // eslint-disable-next-line
  }, []);

  const getCategoryIcon = (category) => {
    const icons = {
      general: 'fas fa-globe',
      business: 'fas fa-briefcase',
      entertainment: 'fas fa-film',
      health: 'fas fa-heartbeat',
      science: 'fas fa-flask',
      sports: 'fas fa-football-ball',
      technology: 'fas fa-microchip'
    };
    return icons[category] || 'fas fa-newspaper';
  };

  return (
    <div className="news-page">
      {/* Hero Section */}
      <div className="hero-section bg-gradient-primary text-white py-4 mb-4">
        <div className="container">
          <div className="row align-items-center">
            <div className="col-lg-8">
              <h1 className="display-5 fw-bold mb-2">
                <i className={`${getCategoryIcon(props.category)} me-3`}></i>
                {capitalizeFirstLetter(props.category)} News
              </h1>
              <p className="lead mb-0">
                Stay updated with the latest {props.category} headlines from around the world
              </p>
            </div>
            <div className="col-lg-4 text-center">
              <i className="fas fa-newspaper display-1 opacity-50"></i>
            </div>
          </div>
        </div>
      </div>

      {loading && <Spinner />}
      
      <InfiniteScroll
        dataLength={articles ? articles.length : 0}
        next={fetchMoreData}
        hasMore={articles.length < totalResults}
        loader={<Spinner />}
      >
        <div className="container">
          {articles.length > 0 && (
            <div className="row mb-3">
              <div className="col-12">
                <p className="text-muted">
                  <i className="fas fa-info-circle me-2"></i>
                  Showing {articles.length} of {totalResults} articles
                </p>
              </div>
            </div>
          )}
          
          <div className="row g-4">
            {articles.map((element, index) => (
              <div className="col-lg-4 col-md-6" key={element.url || index}>
                <NewsItems
                  title={element.title ? element.title.slice(0, 60) : "No Title Available"}
                  description={
                    element.description ? element.description.slice(0, 120) : "No Description Available"
                  }
                  imageUrl={element.urlToImage}
                  newsUrl={element.url}
                  author={element.author}
                  date={element.publishedAt}
                  source={element.source?.name}
                />
              </div>
            ))}
          </div>
          
          {articles.length === 0 && !loading && (
            <div className="text-center py-5">
              <i className="fas fa-newspaper fa-3x text-muted mb-3"></i>
              <h4 className="text-muted">No articles found</h4>
              <p className="text-muted">Please try again later or check your internet connection.</p>
            </div>
          )}
        </div>
      </InfiniteScroll>
    </div>
  );
};

News.defaultProps = {
  country: "us",
  pageSize: 8,
  category: "general",
};

News.propTypes = {
  country: PropTypes.string,
  pageSize: PropTypes.number,
  category: PropTypes.string,
  setProgress: PropTypes.func.isRequired,
};

export default News;
