// Generic API request function with error handling
export const apiRequest = async (url, options = {}) => {
  try {
    const response = await fetch(url, {
      headers: {
        'Content-Type': 'application/json',
        ...options.headers
      },
      ...options
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const data = await response.json();
    return { success: true, data };
  } catch (error) {
    console.error('API request failed:', error);
    return { success: false, error: error.message };
  }
};

// News API
export const newsAPI = {
  async getNews(params = {}) {
    try {
      const queryParams = new URLSearchParams();

      if (params.category && params.category !== 'all') {
        queryParams.append('category', params.category);
      }
      if (params.country) {
        queryParams.append('country', params.country);
      }
      if (params.q) {
        queryParams.append('q', params.q);
      }
      if (params.page) {
        queryParams.append('page', params.page);
      }
      if (params.pageSize || params.limit) {
        queryParams.append('pageSize', params.pageSize || params.limit);
      }

      const url = `${API_CONFIG.NEWS_FEED.baseURL}${API_CONFIG.NEWS_FEED.endpoints.news}${queryParams.toString() ? '?' + queryParams.toString() : ''}`;
      const response = await fetch(url);
      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to fetch news');
      }

      // Transform NewsAPI response to match frontend expectations
      return {
        articles: data.articles?.map(article => ({
          id: article.url || Math.random().toString(36).substr(2, 9),
          title: article.title,
          summary: article.description || (article.content ? article.content.substring(0, 200) + '...' : 'No description available'),
          source: article.source?.name || 'Unknown Source',
          publishedAt: article.publishedAt,
          url: article.url,
          category: params.category || 'general',
          image: article.urlToImage || 'https://via.placeholder.com/300x200?text=No+Image',
          credibilityScore: Math.floor(Math.random() * 20) + 75, // Mock credibility score between 75-95
          author: article.author
        })) || [],
        totalResults: data.totalResults || 0,
        status: data.status || 'ok'
      };
    } catch (error) {
      console.error('News API Error:', error);
      throw error;
    }
  }
};

// Fact Checker API
export const factCheckerAPI = {
  checkFact: async (text) => {
    try {
      const response = await apiRequest(
        `${API_CONFIG.FACT_CHECKER.baseURL}${API_CONFIG.FACT_CHECKER.endpoints.check}`,
        {
          method: 'POST',
          body: JSON.stringify({ text })
        }
      );

      // Transform response to match frontend expectations
      return {
        verdict: response.final_verdict || 'Unable to determine',
        confidence: response.credibility_score || 0,
        sources: response.supporting_articles?.map(article => ({
          title: article.title || 'Unknown',
          url: article.link || '#',
          credibility: 80 // Default credibility
        })) || [],
        explanation: response.reasoning || 'No explanation available',
        timestamp: new Date().toISOString(),
        executionTime: response.execution_time || 0,
        confidenceLevel: response.confidence_level || 'Low',
        claimAnalysis: response.claim_analysis || {},
        summary: response.summary || '',
        biasAnalysis: response.bias_analysis || {}
      };
    } catch (error) {
      console.warn('Fact checker API unavailable, using mock data');
      // Mock response for development
      return {
        verdict: 'Service Unavailable',
        confidence: 0,
        sources: [],
        explanation: 'Fact checking service is currently unavailable. Please try again later.',
        timestamp: new Date().toISOString()
      };
    }
  }
};

