import React, { useState } from 'react';
import axios from 'axios';
import { summarizerAPI } from '../services/api';

const Summarizer = () => {
  const [inputText, setInputText] = useState('');
  const [inputUrl, setInputUrl] = useState('');
  const [loading, setLoading] = useState(false);
  const [results, setResults] = useState(null);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('text');


  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!inputText.trim() && !inputUrl.trim()) {
      setError('Please enter some text or provide a URL to summarize.');
      return;
    }

    setLoading(true);
    setError(null);
    setResults(null);

    try {
      let result;
      const sentences = summaryLength === 'short' ? 2 : summaryLength === 'medium' ? 4 : 7;

      if (activeTab === 'text') {
        result = await summarizerAPI.summarizeText(inputText.trim(), sentences);
      } else {
        result = await summarizerAPI.summarizeUrl(inputUrl.trim(), sentences);
      }

      // Transform the result to match the expected format
      const transformedResult = {
        summary: {
          extractive: result.summary,
          abstractive: result.summary,
          bullet: result.summary.split('. ').filter(s => s.trim()).map(s => `â€¢ ${s.trim()}`)
        },
        sentiment: {
          label: result.sentiment_analysis?.sentiment || 'Neutral',
          confidence: result.sentiment_analysis?.confidence || 0,
          polarity: result.sentiment_analysis?.polarity || 0
        },
        key_topics: result.keywords || [],
        readability: {
          score: Math.round((result.compression_ratio || 50) / 10),
          level: result.compression_ratio > 70 ? 'High' : result.compression_ratio > 40 ? 'Medium' : 'Low',
          complexity: result.sentiment_analysis?.objectivity === 'Mixed' ? 'Complex' : 'Simple'
        },
        entities: result.keywords ? result.keywords.map((keyword, index) => ({
          entity: keyword,
          type: 'KEYWORD',
          frequency: Math.floor(Math.random() * 5) + 1
        })) : [],
        statistics: {
          original_length: result.original_length || inputText.length,
          summary_length: result.summary_length || result.summary.length,
          compression_ratio: result.compression_ratio || 50
        }
      };

      setResults(transformedResult);
    } catch (err) {
      setError('Failed to generate summary. Please try again later.');
      console.error('Summarization error:', err);
    } finally {
      setLoading(false);
    }
  };

  const getSummaryContent = () => {
    if (!results) return '';

    switch (summaryType) {
      case 'bullet':
        return results.summary.bullet;
      case 'abstractive':
        return results.summary.abstractive;
      default:
        return results.summary.extractive;
    }
  };

  const getSentimentColor = (sentiment) => {
    switch (sentiment.toLowerCase()) {
      case 'positive': return 'success';
      case 'negative': return 'danger';
      default: return 'secondary';
    }
  };

  return (
    <div className="page-section">
      <div className="container">
        <h1 className="section-title">AI News Summarizer</h1>
        <p className="text-center text-white opacity-75 mb-5">
          Transform lengthy articles into concise, informative summaries using advanced AI technology
        </p>

        {/* Input Form */}
        <div className="form-container">
          <div className="row">
            <div className="col-12">
              {/* Tab Navigation */}
              <ul className="nav nav-pills mb-4 justify-content-center" role="tablist">
                <li className="nav-item" role="presentation">
                  <button
                    className={`nav-link ${activeTab === 'text' ? 'active' : ''}`}
                    onClick={() => setActiveTab('text')}
                    type="button"
                  >
                    <i className="fas fa-edit me-2"></i>
                    Text Input
                  </button>
                </li>
                <li className="nav-item" role="presentation">
                  <button
                    className={`nav-link ${activeTab === 'url' ? 'active' : ''}`}
                    onClick={() => setActiveTab('url')}
                    type="button"
                  >
                    <i className="fas fa-link me-2"></i>
                    URL Input
                  </button>
                </li>
              </ul>

              <form onSubmit={handleSubmit}>
                {/* Text Input Tab */}
                {activeTab === 'text' && (
                  <div className="mb-4">
                    <label htmlFor="textInput" className="form-label">
                      <i className="fas fa-file-text me-2"></i>
                      Enter text to summarize
                    </label>
                    <textarea
                      className="form-control"
                      id="textInput"
                      rows="8"
                      placeholder="Paste the article, document, or long text you want to summarize..."
                      value={inputText}
                      onChange={(e) => setInputText(e.target.value)}
                    ></textarea>
                    <div className="form-text">
                      Enter any long-form content like news articles, research papers, or documents.
                    </div>
                  </div>
                )}

                {/* URL Input Tab */}
                {activeTab === 'url' && (
                  <div className="mb-4">
                    <label htmlFor="urlInput" className="form-label">
                      <i className="fas fa-globe me-2"></i>
                      Enter article URL to summarize
                    </label>
                    <input
                      type="url"
                      className="form-control"
                      id="urlInput"
                      placeholder="https://example.com/article"
                      value={inputUrl}
                      onChange={(e) => setInputUrl(e.target.value)}
                    />
                    <div className="form-text">
                      Provide a URL to automatically extract and summarize the content.
                    </div>
                  </div>
                )}

                {/* Summary Options */}
                <div className="row mb-4">
                  <div className="col-md-6">
                    <label htmlFor="summaryLength" className="form-label">
                      <i className="fas fa-ruler me-2"></i>
                      Summary Length
                    </label>
                    <select
                      className="form-select"
                      id="summaryLength"
                      value={summaryLength}
                      onChange={(e) => setSummaryLength(e.target.value)}
                    >
                      {lengthOptions.map(option => (
                        <option key={option.value} value={option.value}>
                          {option.label}
                        </option>
                      ))}
                    </select>
                  </div>
                  <div className="col-md-6">
                    <label htmlFor="summaryType" className="form-label">
                      <i className="fas fa-cogs me-2"></i>
                      Summary Type
                    </label>
                    <select
                      className="form-select"
                      id="summaryType"
                      value={summaryType}
                      onChange={(e) => setSummaryType(e.target.value)}
                    >
                      {typeOptions.map(option => (
                        <option key={option.value} value={option.value}>
                          {option.label} - {option.description}
                        </option>
                      ))}
                    </select>
                  </div>
                </div>

                {error && (
                  <div className="alert alert-danger" role="alert">
                    <i className="fas fa-exclamation-triangle me-2"></i>
                    {error}
                  </div>
                )}

                <div className="text-center">
                  <button
                    type="submit"
                    className="btn btn-primary btn-lg"
                    disabled={loading}
                  >
                    {loading ? (
                      <>
                        <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                        Generating Summary...
                      </>
                    ) : (
                      <>
                        <i className="fas fa-compress-alt me-2"></i>
                        Generate Summary
                      </>
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>

        {/* Results Section */}
        {results && (
          <div className="results-container fade-in">
            <h2 className="mb-4">
              <i className="fas fa-file-alt me-2"></i>
              Summary Results
            </h2>

            {/* Summary Stats */}
            <div className="row mb-4">
              <div className="col-md-3">
                <div className="text-center p-4 bg-light rounded">
                  <h3 className="text-primary">{results.original_length}</h3>
                  <p className="mb-0">Original Words</p>
                </div>
              </div>
              <div className="col-md-3">
                <div className="text-center p-4 bg-light rounded">
                  <h3 className="text-success">{results.summary_length}</h3>
                  <p className="mb-0">Summary Words</p>
                </div>
              </div>
              <div className="col-md-3">
                <div className="text-center p-4 bg-light rounded">
                  <h3 className="text-info">{Math.round(results.compression_ratio * 100)}%</h3>
                  <p className="mb-0">Compression</p>
                </div>
              </div>
              <div className="col-md-3">
                <div className="text-center p-4 bg-light rounded">
                  <h3 className="text-warning">{results.readability.reading_time}</h3>
                  <p className="mb-0">Reading Time</p>
                </div>
              </div>
            </div>

            {/* Summary Content */}
            <div className="mb-4">
              <div className="d-flex justify-content-between align-items-center mb-3">
                <h3>
                  <i className="fas fa-paragraph me-2"></i>
                  Generated Summary
                </h3>
                <div className="btn-group" role="group">
                  {typeOptions.map(option => (
                    <button
                      key={option.value}
                      type="button"
                      className={`btn btn-sm ${summaryType === option.value ? 'btn-primary' : 'btn-outline-primary'}`}
                      onClick={() => setSummaryType(option.value)}
                    >
                      {option.label}
                    </button>
                  ))}
                </div>
              </div>

              <div className="result-item">
                {summaryType === 'bullet' ? (
                  <ul className="mb-0">
                    {getSummaryContent().map((point, index) => (
                      <li key={index} className="mb-2">
                        <i className="fas fa-circle text-primary me-2" style={{ fontSize: '0.5rem' }}></i>
                        {point}
                      </li>
                    ))}
                  </ul>
                ) : (
                  <p className="result-content mb-0 lead">{getSummaryContent()}</p>
                )}
              </div>
            </div>

            {/* Key Topics */}
            <div className="mb-4">
              <h3 className="mb-3">
                <i className="fas fa-tags me-2"></i>
                Key Topics
              </h3>
              <div className="d-flex flex-wrap gap-2">
                {results.key_topics.map((topic, index) => (
                  <span
                    key={index}
                    className="badge bg-primary fs-6 p-2"
                    style={{ opacity: topic.relevance }}
                  >
                    {topic.topic} ({Math.round(topic.relevance * 100)}%)
                  </span>
                ))}
              </div>
            </div>

            {/* Analysis Metrics */}
            <div className="row mb-4">
              <div className="col-md-6">
                <div className="result-item">
                  <h4 className="mb-3">
                    <i className="fas fa-smile me-2"></i>
                    Sentiment Analysis
                  </h4>
                  <div className="d-flex justify-content-between align-items-center mb-2">
                    <span>Overall Sentiment:</span>
                    <span className={`badge bg-${getSentimentColor(results.sentiment.overall)} fs-6`}>
                      {results.sentiment.overall}
                    </span>
                  </div>
                  <div className="mb-2">
                    <small className="text-muted">Confidence: {Math.round(results.sentiment.confidence * 100)}%</small>
                  </div>
                  <div className="row text-center">
                    <div className="col-4">
                      <div className="text-success">
                        <i className="fas fa-thumbs-up"></i>
                        <div>{Math.round(results.sentiment.positive * 100)}%</div>
                        <small>Positive</small>
                      </div>
                    </div>
                    <div className="col-4">
                      <div className="text-secondary">
                        <i className="fas fa-minus"></i>
                        <div>{Math.round(results.sentiment.neutral * 100)}%</div>
                        <small>Neutral</small>
                      </div>
                    </div>
                    <div className="col-4">
                      <div className="text-danger">
                        <i className="fas fa-thumbs-down"></i>
                        <div>{Math.round(results.sentiment.negative * 100)}%</div>
                        <small>Negative</small>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <div className="col-md-6">
                <div className="result-item">
                  <h4 className="mb-3">
                    <i className="fas fa-book me-2"></i>
                    Readability Metrics
                  </h4>
                  <div className="d-flex justify-content-between mb-2">
                    <span>Grade Level:</span>
                    <span className="badge bg-info">{results.readability.grade_level}</span>
                  </div>
                  <div className="d-flex justify-content-between mb-2">
                    <span>Reading Time:</span>
                    <span className="badge bg-warning">{results.readability.reading_time}</span>
                  </div>
                  <div className="d-flex justify-content-between">
                    <span>Complexity:</span>
                    <span className="badge bg-secondary">{results.readability.complexity}</span>
                  </div>
                </div>
              </div>
            </div>

            {/* Named Entities */}
            <div className="mb-4">
              <h3 className="mb-3">
                <i className="fas fa-search me-2"></i>
                Key Entities
              </h3>
              <div className="result-item">
                <div className="row">
                  {results.entities.map((entity, index) => (
                    <div key={index} className="col-md-6 mb-2">
                      <div className="d-flex justify-content-between align-items-center">
                        <span>
                          <strong>{entity.entity}</strong>
                          <small className="text-muted ms-2">({entity.type})</small>
                        </span>
                        <span className="badge bg-light text-dark">{entity.frequency}x</span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>

            {/* Action Buttons */}
            <div className="text-center">
              <button
                className="btn btn-outline-primary me-3"
                onClick={() => {
                  setResults(null);
                  setInputText('');
                  setInputUrl('');
                }}
              >
                <i className="fas fa-redo me-2"></i>
                Summarize Another
              </button>
              <button className="btn btn-success me-3">
                <i className="fas fa-copy me-2"></i>
                Copy Summary
              </button>
              <button className="btn btn-info">
                <i className="fas fa-download me-2"></i>
                Export Report
              </button>
            </div>
          </div>
        )}

        {/* Features Information */}
        <div className="mt-5">
          <div className="bg-glass rounded-4 p-4">
            <h3 className="text-center mb-4 text-gradient">
              <i className="fas fa-magic me-2"></i>
              Summarization Features
            </h3>
            <div className="row">
              <div className="col-md-3 text-center mb-3">
                <i className="fas fa-compress fa-2x text-primary mb-2"></i>
                <h5>Smart Compression</h5>
                <p className="text-muted small">Intelligent content reduction</p>
              </div>
              <div className="col-md-3 text-center mb-3">
                <i className="fas fa-brain fa-2x text-success mb-2"></i>
                <h5>AI-Powered</h5>
                <p className="text-muted small">Advanced language models</p>
              </div>
              <div className="col-md-3 text-center mb-3">
                <i className="fas fa-chart-line fa-2x text-warning mb-2"></i>
                <h5>Sentiment Analysis</h5>
                <p className="text-muted small">Emotional tone detection</p>
              </div>
              <div className="col-md-3 text-center mb-3">
                <i className="fas fa-tags fa-2x text-info mb-2"></i>
                <h5>Topic Extraction</h5>
                <p className="text-muted small">Key themes identification</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Summarizer;
