import React, { useState } from 'react';
import { biasCheckerAPI } from '../services/api';
import './BiasChecker.css';

const BiasChecker = () => {
  const [activeTab, setActiveTab] = useState('text');
  const [textInput, setTextInput] = useState('');
  const [urlInput, setUrlInput] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleAnalyze = async () => {
    if (activeTab === 'text' && !textInput.trim()) {
      setError('Please enter some text to analyze');
      return;
    }
    if (activeTab === 'url' && !urlInput.trim()) {
      setError('Please enter a URL to analyze');
      return;
    }

    setLoading(true);
    setError('');
    setResult(null);

    try {
      const isUrl = activeTab === 'url';
      const input = isUrl ? urlInput : textInput;
      const response = await biasCheckerAPI.checkBias(input, isUrl);
      setResult(response);
    } catch (err) {
      setError('Failed to analyze bias. Please try again.');
      console.error('Bias analysis error:', err);
    } finally {
      setLoading(false);
    }
  };

  const getBiasColor = (bias) => {
    const colors = {
      'Left': '#ff4444',
      'Left-Center': '#ff8844',
      'Least Biased': '#44ff44',
      'Right-Center': '#4488ff',
      'Right': '#8844ff'
    };
    return colors[bias] || '#666';
  };

  return (
    <div className="bias-checker">
      <div className="bias-checker-header">
        <h1>AI Bias Detector</h1>
        <p>Analyze political bias in news articles and text content</p>
      </div>

      <div className="bias-checker-tabs">
        <button 
          className={`tab ${activeTab === 'text' ? 'active' : ''}`}
          onClick={() => setActiveTab('text')}
        >
          Text Analysis
        </button>
        <button 
          className={`tab ${activeTab === 'url' ? 'active' : ''}`}
          onClick={() => setActiveTab('url')}
        >
          URL Analysis
        </button>
      </div>

      <div className="bias-checker-content">
        {activeTab === 'text' ? (
          <div className="input-section">
            <label>Enter text to analyze:</label>
            <textarea
              value={textInput}
              onChange={(e) => setTextInput(e.target.value)}
              placeholder="Paste your news article or text content here..."
              rows={8}
            />
          </div>
        ) : (
          <div className="input-section">
            <label>Enter article URL:</label>
            <input
              type="url"
              value={urlInput}
              onChange={(e) => setUrlInput(e.target.value)}
              placeholder="https://example.com/news-article"
            />
          </div>
        )}

        <button 
          className="analyze-btn"
          onClick={handleAnalyze}
          disabled={loading}
        >
          {loading ? 'Analyzing...' : 'Analyze Bias'}
        </button>

        {error && (
          <div className="error-message">
            {error}
          </div>
        )}

        {result && (
          <div className="bias-results">
            <h3>Bias Analysis Results</h3>

            {/* Bias Analysis Results Section */}
            <div className="result-card bias-analysis">
              <h4>Bias Analysis Results</h4>
              <div className="bias-score-section">
                <div className="bias-score">
                  <span className="score-label">Bias Score:</span>
                  <span className="score-value">{result['Bias Analysis Results']['Bias Score']}</span>
                </div>
                <div className="bias-description">
                  <span className="description-label">Description:</span>
                  <span className="description-value">{result['Bias Analysis Results']['Description']}</span>
                </div>
              </div>
            </div>

            {/* Detailed Bias Indicators Section */}
            <div className="result-card indicators">
              <h4>Detailed Bias Indicators</h4>
              <div className="indicators-grid">
                {Object.entries(result['Detailed Bias Indicators']).map(([key, value]) => (
                  <div key={key} className="indicator-item">
                    <span className="indicator-name">{key}:</span>
                    <span className={`indicator-status ${value.Status.toLowerCase().replace(' ', '-')}`}>
                      {value.Status}
                    </span>
                  </div>
                ))}
              </div>
            </div>

            {/* Potentially Biased Language Section */}
            {result['Potentially Biased Language'] && result['Potentially Biased Language'].length > 0 && (
              <div className="result-card biased-language">
                <h4>Potentially Biased Language</h4>
                <div className="biased-language-list">
                  {result['Potentially Biased Language'].map((item, index) => (
                    <div key={index} className="biased-item">
                      <div className="biased-header">
                        <span className={`indicator-type ${item.indicator.toLowerCase()}`}>
                          {item.indicator}
                        </span>
                        <span className="biased-text">{item.language}</span>
                      </div>
                      <p className="suggestion">{item.suggestion}</p>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Recommendations Section */}
            {result['Recommendations for Improvement'] && result['Recommendations for Improvement'].length > 0 && (
              <div className="result-card recommendations">
                <h4>Recommendations for Improvement</h4>
                <ul className="recommendations-list">
                  {result['Recommendations for Improvement'].map((recommendation, index) => (
                    <li key={index} className="recommendation-item">{recommendation}</li>
                  ))}
                </ul>
              </div>
            )}

            {/* Analysis Summary Section */}
            {result['Analysis Summary'] && (
              <div className="result-card summary">
                <h4>Analysis Summary</h4>
                <p className="summary-text">{result['Analysis Summary']}</p>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default BiasChecker;
