import React, { useState } from 'react';
import geminiService from '../services/geminiService';

const ApiTest = () => {
  const [testResult, setTestResult] = useState('');
  const [loading, setLoading] = useState(false);

  const testApi = async () => {
    setLoading(true);
    setTestResult('Testing API...');

    try {
      console.log('Starting API test...');
      const result = await geminiService.summarizeNews('This is a test news article about technology trends.');
      console.log('API test result:', result);
      setTestResult(`Success: ${JSON.stringify(result, null, 2)}`);
    } catch (error) {
      console.error('API test failed:', error);
      setTestResult(`Error: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const testBackendHealth = async () => {
    setLoading(true);
    setTestResult('Testing backend health...');

    try {
      const response = await fetch('http://localhost:3001/health');
      const data = await response.json();
      setTestResult(`Backend Health: ${JSON.stringify(data, null, 2)}`);
    } catch (error) {
      setTestResult(`Backend Health Error: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mt-4">
      <div className="card">
        <div className="card-header">
          <h3>API Test Component</h3>
        </div>
        <div className="card-body">
          <div className="mb-3">
            <button 
              className="btn btn-primary me-2" 
              onClick={testBackendHealth}
              disabled={loading}
            >
              {loading ? 'Testing...' : 'Test Backend Health'}
            </button>
            <button 
              className="btn btn-success" 
              onClick={testApi}
              disabled={loading}
            >
              {loading ? 'Testing...' : 'Test Gemini API'}
            </button>
          </div>
          <div className="mt-3">
            <h5>Result:</h5>
            <pre className="bg-light p-3 rounded">
              {testResult || 'No test run yet'}
            </pre>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ApiTest;
