export const biasCheckerAPI = {
  checkBias: async (text) => {
    try {
      const response = await apiRequest(
        `${API_CONFIG.BIAS_CHECKER.baseURL}${API_CONFIG.BIAS_CHECKER.endpoints.check}`,
