function _getBiasRecommendation(biasCategory) {
  const recommendations = {
    'Left': 'This content shows a left-leaning bias. Consider reading conservative sources for balance.',
    'Left-Center': 'This content has a slight left-leaning bias. Generally reliable but consider diverse sources.',
    'Least Biased': 'This content appears to be relatively unbiased and factual.',
