import React, { useState } from 'react';
import axios from 'axios';

const FactChecker = () => {
  const [inputText, setInputText] = useState('');
  const [inputUrl, setInputUrl] = useState('');
  const [loading, setLoading] = useState(false);
  const [results, setResults] = useState(null);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('text');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!inputText.trim() && !inputUrl.trim()) {
      setError('Please enter some text or provide a URL to fact-check.');
      return;
    }

    setLoading(true);
    setError(null);
    setResults(null);

    try {
      // Simulate API call to fact-checking service
      await new Promise(resolve => setTimeout(resolve, 2000));

      // Mock response (replace with actual API call)
      const mockResults = {
        overall_score: 0.85,
        credibility: 'High',
        claims: [
          {
            claim: "Climate change is causing global temperatures to rise",
            verdict: "True",
            confidence: 0.95,
            sources: [
              "NASA Climate Change Report 2023",
              "IPCC Assessment Report",
              "National Weather Service Data"
            ],
            explanation: "Multiple scientific organizations and peer-reviewed studies confirm that global temperatures have been rising due to increased greenhouse gas emissions."
          },
          {
            claim: "Renewable energy is becoming more cost-effective",
            verdict: "True",
            confidence: 0.88,
            sources: [
              "International Energy Agency Report",
              "Bloomberg Energy Finance",
              "Department of Energy Statistics"
            ],
            explanation: "Recent data shows that renewable energy costs have decreased significantly, making them competitive with traditional fossil fuels."
          }
        ],
        summary: "The provided content contains factually accurate information supported by credible sources. The claims about climate change and renewable energy are well-documented and verified by multiple authoritative sources.",
        bias_analysis: {
          political_lean: "Neutral",
          emotional_language: "Low",
          factual_reporting: "High"
        }
      };

      setResults(mockResults);
    } catch (err) {
      setError('Failed to analyze the content. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const getScoreColor = (score) => {
    if (score >= 0.8) return 'success';
    if (score >= 0.6) return 'warning';
    return 'danger';
  };

  const getVerdictColor = (verdict) => {
    switch (verdict.toLowerCase()) {
      case 'true': return 'success';
      case 'false': return 'danger';
      case 'partially true': return 'warning';
      default: return 'secondary';
    }
  };

  return (
    <div className="page-section">
      <div className="container">
        <h1 className="section-title">AI Fact Checker</h1>
        <p className="text-center text-white opacity-75 mb-5">
          Verify the accuracy of news articles, claims, and statements using advanced AI analysis
        </p>

        {/* Input Form */}
        <div className="form-container">
          <div className="row">
            <div className="col-12">
              {/* Tab Navigation */}
              <ul className="nav nav-pills mb-4 justify-content-center" role="tablist">
                <li className="nav-item" role="presentation">
                  <button
                    className={`nav-link ${activeTab === 'text' ? 'active' : ''}`}
                    onClick={() => setActiveTab('text')}
                    type="button"
                  >
                    <i className="fas fa-edit me-2"></i>
                    Text Input
                  </button>
                </li>
                <li className="nav-item" role="presentation">
                  <button
                    className={`nav-link ${activeTab === 'url' ? 'active' : ''}`}
                    onClick={() => setActiveTab('url')}
                    type="button"
                  >
                    <i className="fas fa-link me-2"></i>
                    URL Input
                  </button>
                </li>
              </ul>

              <form onSubmit={handleSubmit}>
                {/* Text Input Tab */}
                {activeTab === 'text' && (
                  <div className="mb-4">
                    <label htmlFor="textInput" className="form-label">
                      <i className="fas fa-quote-left me-2"></i>
                      Enter text to fact-check
                    </label>
                    <textarea
                      className="form-control"
                      id="textInput"
                      rows="6"
                      placeholder="Paste the text, article, or claim you want to fact-check..."
                      value={inputText}
                      onChange={(e) => setInputText(e.target.value)}
                    ></textarea>
                    <div className="form-text">
                      Enter any text content, news article, or specific claims you want to verify.
                    </div>
                  </div>
                )}

                {/* URL Input Tab */}
                {activeTab === 'url' && (
                  <div className="mb-4">
                    <label htmlFor="urlInput" className="form-label">
                      <i className="fas fa-globe me-2"></i>
                      Enter article URL
                    </label>
                    <input
                      type="url"
                      className="form-control"
                      id="urlInput"
                      placeholder="https://example.com/news-article"
                      value={inputUrl}
                      onChange={(e) => setInputUrl(e.target.value)}
                    />
                    <div className="form-text">
                      Provide a URL to a news article or web page for fact-checking.
                    </div>
                  </div>
                )}

                {error && (
                  <div className="alert alert-danger" role="alert">
                    <i className="fas fa-exclamation-triangle me-2"></i>
                    {error}
                  </div>
                )}

                <div className="text-center">
                  <button
                    type="submit"
                    className="btn btn-primary btn-lg"
                    disabled={loading}
                  >
                    {loading ? (
                      <>
                        <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                        Analyzing...
                      </>
                    ) : (
                      <>
                        <i className="fas fa-search me-2"></i>
                        Fact Check
                      </>
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>

        {/* Results Section */}
        {results && (
          <div className="results-container fade-in">
            <h2 className="mb-4">
              <i className="fas fa-chart-line me-2"></i>
              Fact-Check Results
            </h2>

            {/* Overall Score */}
            <div className="row mb-4">
              <div className="col-md-4">
                <div className="text-center p-4 bg-light rounded">
                  <h3 className={`text-${getScoreColor(results.overall_score)}`}>
                    {Math.round(results.overall_score * 100)}%
                  </h3>
                  <p className="mb-0">Overall Credibility</p>
                </div>
              </div>
              <div className="col-md-4">
                <div className="text-center p-4 bg-light rounded">
                  <h3 className="text-primary">{results.credibility}</h3>
                  <p className="mb-0">Reliability Rating</p>
                </div>
              </div>
              <div className="col-md-4">
                <div className="text-center p-4 bg-light rounded">
                  <h3 className="text-info">{results.claims.length}</h3>
                  <p className="mb-0">Claims Analyzed</p>
                </div>
              </div>
            </div>

            {/* Claims Analysis */}
            <div className="mb-4">
              <h3 className="mb-3">
                <i className="fas fa-list-check me-2"></i>
                Detailed Claim Analysis
              </h3>
              {results.claims.map((claim, index) => (
                <div key={index} className="result-item mb-3">
                  <div className="d-flex justify-content-between align-items-start mb-2">
                    <h5 className="result-title mb-0">Claim {index + 1}</h5>
                    <span className={`badge bg-${getVerdictColor(claim.verdict)} fs-6`}>
                      {claim.verdict}
                    </span>
                  </div>
                  <blockquote className="blockquote mb-3">
                    <p className="mb-0">"{claim.claim}"</p>
                  </blockquote>
                  <div className="mb-3">
                    <strong>Confidence:</strong>
                    <div className="progress mt-1" style={{ height: '8px' }}>
                      <div
                        className={`progress-bar bg-${getScoreColor(claim.confidence)}`}
                        style={{ width: `${claim.confidence * 100}%` }}
                      ></div>
                    </div>
                    <small className="text-muted">{Math.round(claim.confidence * 100)}%</small>
                  </div>
                  <div className="mb-3">
                    <strong>Explanation:</strong>
                    <p className="result-content mt-1">{claim.explanation}</p>
                  </div>
                  <div>
                    <strong>Sources:</strong>
                    <ul className="mt-1 mb-0">
                      {claim.sources.map((source, sourceIndex) => (
                        <li key={sourceIndex} className="text-muted">{source}</li>
                      ))}
                    </ul>
                  </div>
                </div>
              ))}
            </div>

            {/* Summary */}
            <div className="mb-4">
              <h3 className="mb-3">
                <i className="fas fa-summary me-2"></i>
                Summary
              </h3>
              <div className="result-item">
                <p className="result-content mb-0">{results.summary}</p>
              </div>
            </div>

            {/* Bias Analysis */}
            <div className="mb-4">
              <h3 className="mb-3">
                <i className="fas fa-balance-scale me-2"></i>
                Bias Analysis
              </h3>
              <div className="row">
                <div className="col-md-4">
                  <div className="text-center p-3 bg-light rounded">
                    <h5>Political Lean</h5>
                    <span className="badge bg-secondary fs-6">{results.bias_analysis.political_lean}</span>
                  </div>
                </div>
                <div className="col-md-4">
                  <div className="text-center p-3 bg-light rounded">
                    <h5>Emotional Language</h5>
                    <span className="badge bg-success fs-6">{results.bias_analysis.emotional_language}</span>
                  </div>
                </div>
                <div className="col-md-4">
                  <div className="text-center p-3 bg-light rounded">
                    <h5>Factual Reporting</h5>
                    <span className="badge bg-success fs-6">{results.bias_analysis.factual_reporting}</span>
                  </div>
                </div>
              </div>
            </div>

            {/* Action Buttons */}
            <div className="text-center">
              <button
                className="btn btn-outline-primary me-3"
                onClick={() => {
                  setResults(null);
                  setInputText('');
                  setInputUrl('');
                }}
              >
                <i className="fas fa-redo me-2"></i>
                Check Another
              </button>
              <button className="btn btn-success">
                <i className="fas fa-download me-2"></i>
                Export Report
              </button>
            </div>
          </div>
        )}

        {/* How It Works Section */}
        <div className="mt-5">
          <div className="bg-glass rounded-4 p-4">
            <h3 className="text-center mb-4 text-gradient">
              <i className="fas fa-info-circle me-2"></i>
              How Our Fact Checker Works
            </h3>
            <div className="row">
              <div className="col-md-3 text-center mb-3">
                <i className="fas fa-upload fa-2x text-primary mb-2"></i>
                <h5>Submit Content</h5>
                <p className="text-muted small">Enter text or provide a URL</p>
              </div>
              <div className="col-md-3 text-center mb-3">
                <i className="fas fa-brain fa-2x text-primary mb-2"></i>
                <h5>AI Analysis</h5>
                <p className="text-muted small">Advanced algorithms analyze claims</p>
              </div>
              <div className="col-md-3 text-center mb-3">
                <i className="fas fa-database fa-2x text-primary mb-2"></i>
                <h5>Source Verification</h5>
                <p className="text-muted small">Cross-reference with reliable sources</p>
              </div>
              <div className="col-md-3 text-center mb-3">
                <i className="fas fa-chart-bar fa-2x text-primary mb-2"></i>
                <h5>Detailed Report</h5>
                <p className="text-muted small">Get comprehensive results</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FactChecker;
