import React, { useState, useEffect } from 'react';
import axios from 'axios';

const NewsFeed = () => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [category, setCategory] = useState('general');
  const [error, setError] = useState(null);

  const categories = [
    { value: 'general', label: 'General', icon: 'fas fa-globe' },
    { value: 'business', label: 'Business', icon: 'fas fa-briefcase' },
    { value: 'technology', label: 'Technology', icon: 'fas fa-microchip' },
    { value: 'health', label: 'Health', icon: 'fas fa-heartbeat' },
    { value: 'science', label: 'Science', icon: 'fas fa-flask' },
    { value: 'sports', label: 'Sports', icon: 'fas fa-football-ball' },
    { value: 'entertainment', label: 'Entertainment', icon: 'fas fa-film' }
  ];

  // Mock data for demonstration (replace with actual API calls)
  const mockArticles = [
    {
      id: 1,
      title: "AI Revolution in Healthcare: New Breakthrough in Medical Diagnosis",
      description: "Researchers have developed an AI system that can diagnose diseases with 95% accuracy, potentially revolutionizing healthcare delivery worldwide.",
      url: "#",
      urlToImage: "https://via.placeholder.com/400x200/667eea/ffffff?text=AI+Healthcare",
      publishedAt: "2024-01-15T10:30:00Z",
      source: { name: "Tech News" },
      author: "Dr. Sarah Johnson"
    },
    {
      id: 2,
      title: "Climate Change: Global Temperature Reaches Record High",
      description: "Scientists report that global temperatures have reached unprecedented levels, highlighting the urgent need for climate action.",
      url: "#",
      urlToImage: "https://via.placeholder.com/400x200/f093fb/ffffff?text=Climate+News",
      publishedAt: "2024-01-15T08:15:00Z",
      source: { name: "Environmental Times" },
      author: "Michael Green"
    },
    {
      id: 3,
      title: "Space Exploration: Mars Mission Achieves Major Milestone",
      description: "NASA's latest Mars rover has made significant discoveries about the planet's geological history and potential for past life.",
      url: "#",
      urlToImage: "https://via.placeholder.com/400x200/4facfe/ffffff?text=Space+News",
      publishedAt: "2024-01-15T06:45:00Z",
      source: { name: "Space Daily" },
      author: "Dr. Emily Chen"
    },
    {
      id: 4,
      title: "Economic Update: Global Markets Show Strong Recovery",
      description: "Financial markets worldwide are showing signs of robust recovery following recent economic policies and technological innovations.",
      url: "#",
      urlToImage: "https://via.placeholder.com/400x200/43e97b/ffffff?text=Economy+News",
      publishedAt: "2024-01-15T05:20:00Z",
      source: { name: "Financial Herald" },
      author: "Robert Smith"
    }
  ];

  useEffect(() => {
    fetchNews();
  }, [category]);

  const fetchNews = async () => {
    setLoading(true);
    setError(null);

    try {
      // Simulate API call delay
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Filter mock articles based on category (in real implementation, this would be an API call)
      let filteredArticles = mockArticles;
      if (searchTerm) {
        filteredArticles = mockArticles.filter(article => 
          article.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
          article.description.toLowerCase().includes(searchTerm.toLowerCase())
        );
      }

      setArticles(filteredArticles);
    } catch (err) {
      setError('Failed to fetch news. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    fetchNews();
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div className="page-section">
      <div className="container">
        <h1 className="section-title">Latest News Feed</h1>

        {/* Search and Filter Section */}
        <div className="search-container">
          <form onSubmit={handleSearch}>
            <div className="row g-3 align-items-end">
              <div className="col-md-6">
                <label htmlFor="search" className="form-label">
                  <i className="fas fa-search me-2"></i>
                  Search News
                </label>
                <input
                  type="text"
                  className="form-control search-input"
                  id="search"
                  placeholder="Enter keywords..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
              <div className="col-md-4">
                <label htmlFor="category" className="form-label">
                  <i className="fas fa-filter me-2"></i>
                  Category
                </label>
                <select
                  className="form-select"
                  id="category"
                  value={category}
                  onChange={(e) => setCategory(e.target.value)}
                >
                  {categories.map(cat => (
                    <option key={cat.value} value={cat.value}>
                      {cat.label}
                    </option>
                  ))}
                </select>
              </div>
              <div className="col-md-2">
                <button type="submit" className="btn btn-primary w-100 search-btn">
                  <i className="fas fa-search"></i>
                </button>
              </div>
            </div>
          </form>
        </div>

        {/* Category Pills */}
        <div className="category-pills mb-4">
          <div className="d-flex flex-wrap gap-2 justify-content-center">
            {categories.map(cat => (
              <button
                key={cat.value}
                className={`btn btn-sm ${category === cat.value ? 'btn-primary' : 'btn-outline-light'}`}
                onClick={() => setCategory(cat.value)}
              >
                <i className={`${cat.icon} me-1`}></i>
                {cat.label}
              </button>
            ))}
          </div>
        </div>

        {/* Loading State */}
        {loading && (
          <div className="loading-container">
            <div className="spinner-border" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p className="mt-3 text-white">Fetching latest news...</p>
          </div>
        )}

        {/* Error State */}
        {error && (
          <div className="alert alert-danger" role="alert">
            <i className="fas fa-exclamation-triangle me-2"></i>
            {error}
          </div>
        )}

        {/* News Articles */}
        {!loading && !error && (
          <div className="row">
            {articles.length > 0 ? (
              articles.map((article, index) => (
                <div key={article.id} className="col-lg-6 col-xl-4 mb-4">
                  <div className="news-card fade-in" style={{ animationDelay: `${index * 0.1}s` }}>
                    <img
                      src={article.urlToImage || 'https://via.placeholder.com/400x200/667eea/ffffff?text=News'}
                      className="card-img-top"
                      alt={article.title}
                      onError={(e) => {
                        e.target.src = 'https://via.placeholder.com/400x200/667eea/ffffff?text=News';
                      }}
                    />
                    <div className="card-body">
                      <div className="d-flex justify-content-between align-items-start mb-2">
                        <span className="source-badge">{article.source.name}</span>
                        <small className="text-muted">
                          <i className="fas fa-clock me-1"></i>
                          {formatDate(article.publishedAt)}
                        </small>
                      </div>
                      <h5 className="card-title">{article.title}</h5>
                      <p className="card-text">{article.description}</p>
                      <div className="news-meta">
                        <i className="fas fa-user me-1"></i>
                        By {article.author || 'Unknown Author'}
                      </div>
                      <div className="d-flex gap-2">
                        <a
                          href={article.url}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="btn btn-primary btn-sm"
                        >
                          <i className="fas fa-external-link-alt me-1"></i>
                          Read More
                        </a>
                        <button className="btn btn-outline-secondary btn-sm">
                          <i className="fas fa-share me-1"></i>
                          Share
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <div className="col-12">
                <div className="text-center py-5">
                  <i className="fas fa-newspaper fa-3x text-muted mb-3"></i>
                  <h3 className="text-white">No articles found</h3>
                  <p className="text-white opacity-75">Try adjusting your search terms or category filter.</p>
                </div>
              </div>
            )}
          </div>
        )}

        {/* Load More Button */}
        {!loading && articles.length > 0 && (
          <div className="text-center mt-4">
            <button className="btn btn-outline-light btn-lg">
              <i className="fas fa-plus me-2"></i>
              Load More Articles
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default NewsFeed;
