import React, { useState } from 'react';
import axios from 'axios';

const BiasChecker = () => {
  const [inputText, setInputText] = useState('');
  const [inputUrl, setInputUrl] = useState('');
  const [loading, setLoading] = useState(false);
  const [results, setResults] = useState(null);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('text');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!inputText.trim() && !inputUrl.trim()) {
      setError('Please enter some text or provide a URL to analyze for bias.');
      return;
    }

    setLoading(true);
    setError(null);
    setResults(null);

    try {
      // Simulate API call to bias detection service
      await new Promise(resolve => setTimeout(resolve, 2500));

      // Mock response (replace with actual API call)
      const mockResults = {
        overall_bias_score: 0.25, // 0 = no bias, 1 = extreme bias
        bias_direction: 'Slightly Left',
        confidence: 0.78,
        bias_indicators: {
          political_lean: {
            score: 0.3,
            direction: 'Left',
            confidence: 0.75,
            explanation: 'The article shows slight preference for progressive policies and viewpoints.'
          },
          emotional_language: {
            score: 0.4,
            level: 'Moderate',
            confidence: 0.82,
            explanation: 'Contains some emotionally charged words but maintains reasonable objectivity.'
          },
          source_diversity: {
            score: 0.7,
            level: 'Good',
            confidence: 0.85,
            explanation: 'Article cites multiple sources from different perspectives.'
          },
          factual_reporting: {
            score: 0.85,
            level: 'High',
            confidence: 0.90,
            explanation: 'Most claims are supported by verifiable facts and data.'
          }
        },
        biased_phrases: [
          {
            phrase: "clearly demonstrates",
            type: "Certainty Language",
            bias_level: "Low",
            suggestion: "Consider using 'suggests' or 'indicates' for more neutral tone"
          },
          {
            phrase: "experts agree",
            type: "Generalization",
            bias_level: "Medium",
            suggestion: "Specify which experts and acknowledge potential disagreement"
          }
        ],
        recommendations: [
          "Consider including more diverse viewpoints on the topic",
          "Reduce use of absolute statements and generalizations",
          "Add more specific attribution for claims and opinions",
          "Balance emotional language with neutral reporting"
        ],
        summary: "The analyzed content shows a slight left-leaning bias but maintains reasonable journalistic standards. The bias is primarily evident in word choice and framing rather than factual inaccuracies."
      };

      setResults(mockResults);
    } catch (err) {
      setError('Failed to analyze bias. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const getBiasColor = (score) => {
    if (score <= 0.2) return 'success';
    if (score <= 0.5) return 'warning';
    return 'danger';
  };

  const getBiasLevel = (score) => {
    if (score <= 0.2) return 'Minimal';
    if (score <= 0.4) return 'Low';
    if (score <= 0.6) return 'Moderate';
    if (score <= 0.8) return 'High';
    return 'Extreme';
  };

  const getScoreColor = (score) => {
    if (score >= 0.8) return 'success';
    if (score >= 0.6) return 'warning';
    return 'danger';
  };

  return (
    <div className="page-section">
      <div className="container">
        <h1 className="section-title">AI Bias Detector</h1>
        <p className="text-center text-white opacity-75 mb-5">
          Analyze news articles and content for potential bias using advanced AI algorithms
        </p>

        {/* Input Form */}
        <div className="form-container">
          <div className="row">
            <div className="col-12">
              {/* Tab Navigation */}
              <ul className="nav nav-pills mb-4 justify-content-center" role="tablist">
                <li className="nav-item" role="presentation">
                  <button
                    className={`nav-link ${activeTab === 'text' ? 'active' : ''}`}
                    onClick={() => setActiveTab('text')}
                    type="button"
                  >
                    <i className="fas fa-edit me-2"></i>
                    Text Analysis
                  </button>
                </li>
                <li className="nav-item" role="presentation">
                  <button
                    className={`nav-link ${activeTab === 'url' ? 'active' : ''}`}
                    onClick={() => setActiveTab('url')}
                    type="button"
                  >
                    <i className="fas fa-link me-2"></i>
                    URL Analysis
                  </button>
                </li>
              </ul>

              <form onSubmit={handleSubmit}>
                {/* Text Input Tab */}
                {activeTab === 'text' && (
                  <div className="mb-4">
                    <label htmlFor="textInput" className="form-label">
                      <i className="fas fa-align-left me-2"></i>
                      Enter text to analyze for bias
                    </label>
                    <textarea
                      className="form-control"
                      id="textInput"
                      rows="6"
                      placeholder="Paste the article, news content, or text you want to analyze for bias..."
                      value={inputText}
                      onChange={(e) => setInputText(e.target.value)}
                    ></textarea>
                    <div className="form-text">
                      Enter any news article, editorial, or text content for bias analysis.
                    </div>
                  </div>
                )}

                {/* URL Input Tab */}
                {activeTab === 'url' && (
                  <div className="mb-4">
                    <label htmlFor="urlInput" className="form-label">
                      <i className="fas fa-globe me-2"></i>
                      Enter article URL for bias analysis
                    </label>
                    <input
                      type="url"
                      className="form-control"
                      id="urlInput"
                      placeholder="https://example.com/news-article"
                      value={inputUrl}
                      onChange={(e) => setInputUrl(e.target.value)}
                    />
                    <div className="form-text">
                      Provide a URL to analyze the content for potential bias.
                    </div>
                  </div>
                )}

                {error && (
                  <div className="alert alert-danger" role="alert">
                    <i className="fas fa-exclamation-triangle me-2"></i>
                    {error}
                  </div>
                )}

                <div className="text-center">
                  <button
                    type="submit"
                    className="btn btn-primary btn-lg"
                    disabled={loading}
                  >
                    {loading ? (
                      <>
                        <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                        Analyzing Bias...
                      </>
                    ) : (
                      <>
                        <i className="fas fa-balance-scale me-2"></i>
                        Analyze Bias
                      </>
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>

        {/* Results Section */}
        {results && (
          <div className="results-container fade-in">
            <h2 className="mb-4">
              <i className="fas fa-chart-pie me-2"></i>
              Bias Analysis Results
            </h2>

            {/* Overall Bias Score */}
            <div className="row mb-4">
              <div className="col-md-3">
                <div className="text-center p-4 bg-light rounded">
                  <div className={`text-${getBiasColor(results.overall_bias_score)}`}>
                    <i className="fas fa-balance-scale fa-2x mb-2"></i>
                    <h3>{Math.round((1 - results.overall_bias_score) * 100)}%</h3>
                  </div>
                  <p className="mb-0">Objectivity Score</p>
                </div>
              </div>
              <div className="col-md-3">
                <div className="text-center p-4 bg-light rounded">
                  <h3 className={`text-${getBiasColor(results.overall_bias_score)}`}>
                    {getBiasLevel(results.overall_bias_score)}
                  </h3>
                  <p className="mb-0">Bias Level</p>
                </div>
              </div>
              <div className="col-md-3">
                <div className="text-center p-4 bg-light rounded">
                  <h3 className="text-info">{results.bias_direction}</h3>
                  <p className="mb-0">Political Lean</p>
                </div>
              </div>
              <div className="col-md-3">
                <div className="text-center p-4 bg-light rounded">
                  <h3 className="text-primary">{Math.round(results.confidence * 100)}%</h3>
                  <p className="mb-0">Confidence</p>
                </div>
              </div>
            </div>

            {/* Bias Indicators */}
            <div className="mb-4">
              <h3 className="mb-3">
                <i className="fas fa-chart-bar me-2"></i>
                Detailed Bias Indicators
              </h3>
              <div className="row">
                {Object.entries(results.bias_indicators).map(([key, indicator], index) => (
                  <div key={index} className="col-md-6 mb-3">
                    <div className="result-item">
                      <div className="d-flex justify-content-between align-items-center mb-2">
                        <h5 className="result-title mb-0">
                          {key.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase())}
                        </h5>
                        <span className={`badge bg-${getScoreColor(indicator.score)} fs-6`}>
                          {indicator.level || indicator.direction}
                        </span>
                      </div>
                      <div className="mb-2">
                        <div className="progress" style={{ height: '8px' }}>
                          <div
                            className={`progress-bar bg-${getScoreColor(indicator.score)}`}
                            style={{ width: `${indicator.score * 100}%` }}
                          ></div>
                        </div>
                        <small className="text-muted">
                          Score: {Math.round(indicator.score * 100)}% | 
                          Confidence: {Math.round(indicator.confidence * 100)}%
                        </small>
                      </div>
                      <p className="result-content mb-0">{indicator.explanation}</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Biased Phrases */}
            <div className="mb-4">
              <h3 className="mb-3">
                <i className="fas fa-exclamation-circle me-2"></i>
                Potentially Biased Language
              </h3>
              {results.biased_phrases.map((phrase, index) => (
                <div key={index} className="result-item mb-3">
                  <div className="d-flex justify-content-between align-items-start mb-2">
                    <h5 className="result-title mb-0">
                      "{phrase.phrase}"
                    </h5>
                    <div>
                      <span className="badge bg-secondary me-2">{phrase.type}</span>
                      <span className={`badge bg-${phrase.bias_level === 'Low' ? 'success' : phrase.bias_level === 'Medium' ? 'warning' : 'danger'}`}>
                        {phrase.bias_level} Bias
                      </span>
                    </div>
                  </div>
                  <div className="alert alert-info mb-0">
                    <i className="fas fa-lightbulb me-2"></i>
                    <strong>Suggestion:</strong> {phrase.suggestion}
                  </div>
                </div>
              ))}
            </div>

            {/* Recommendations */}
            <div className="mb-4">
              <h3 className="mb-3">
                <i className="fas fa-thumbs-up me-2"></i>
                Recommendations for Improvement
              </h3>
              <div className="result-item">
                <ul className="mb-0">
                  {results.recommendations.map((recommendation, index) => (
                    <li key={index} className="mb-2">
                      <i className="fas fa-check-circle text-success me-2"></i>
                      {recommendation}
                    </li>
                  ))}
                </ul>
              </div>
            </div>

            {/* Summary */}
            <div className="mb-4">
              <h3 className="mb-3">
                <i className="fas fa-summary me-2"></i>
                Analysis Summary
              </h3>
              <div className="result-item">
                <p className="result-content mb-0">{results.summary}</p>
              </div>
            </div>

            {/* Action Buttons */}
            <div className="text-center">
              <button
                className="btn btn-outline-primary me-3"
                onClick={() => {
                  setResults(null);
                  setInputText('');
                  setInputUrl('');
                }}
              >
                <i className="fas fa-redo me-2"></i>
                Analyze Another
              </button>
              <button className="btn btn-success">
                <i className="fas fa-download me-2"></i>
                Export Report
              </button>
            </div>
          </div>
        )}

        {/* Bias Types Information */}
        <div className="mt-5">
          <div className="bg-glass rounded-4 p-4">
            <h3 className="text-center mb-4 text-gradient">
              <i className="fas fa-info-circle me-2"></i>
              Types of Bias We Detect
            </h3>
            <div className="row">
              <div className="col-md-3 text-center mb-3">
                <i className="fas fa-vote-yea fa-2x text-primary mb-2"></i>
                <h5>Political Bias</h5>
                <p className="text-muted small">Left/right political leanings</p>
              </div>
              <div className="col-md-3 text-center mb-3">
                <i className="fas fa-heart fa-2x text-danger mb-2"></i>
                <h5>Emotional Language</h5>
                <p className="text-muted small">Emotionally charged words</p>
              </div>
              <div className="col-md-3 text-center mb-3">
                <i className="fas fa-users fa-2x text-warning mb-2"></i>
                <h5>Source Diversity</h5>
                <p className="text-muted small">Variety of perspectives</p>
              </div>
              <div className="col-md-3 text-center mb-3">
                <i className="fas fa-fact-check fa-2x text-success mb-2"></i>
                <h5>Factual Reporting</h5>
                <p className="text-muted small">Evidence-based claims</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BiasChecker;
